import React, { useEffect, useState } from 'react'
import { Grid, Slide, Typography } from "@material-ui/core";
import BirthDate from './BirthDate';
import RadioGender from './RadioGender';
import NameField from './NameField';

const NameForm = ({ name, setName, error }) => (
    <Grid>
        <Typography>Entrez votre nom complet</Typography>
        <NameField name={name} setName={setName} error={error} />
    </Grid>
);

const GenderForm = ({ gender, setGender }) => (
    <Grid>
        <Typography>Selectionnez votre genre</Typography>
        <RadioGender gender={gender} setGender={setGender} />
    </Grid>
);

const BirthForm = ({ birth, setBirth }) => (
    <Grid>
        <Typography>Entrez votre date de naissance</Typography>
        <BirthDate birth={birth} setBirth={setBirth} />
    </Grid>
);

const EndForm = ({ name, gender, birth }) => (
    <Grid>
        <Typography>Vous êtes {name}, votre genre est {gender} et vous êtes {gender === 'female' ? "née" : "né"} le {new Date(birth).toLocaleDateString()} </Typography>
    </Grid>
);

const FormsStep = ({ step, name, setName, nameError, gender, setGender, birth, setBirth }) => {
    const [slides, setSlides] = useState({
        begin: true,
        name: false,
        gender: false,
        birth: false,
        end: false
    });

    const setSlidesToFalse = () => {
        setSlides({
            begin: false,
            name: false,
            gender: false,
            birth: false,
            end: false
        });
    }

    useEffect(() => {
        setSlidesToFalse();
        switch (step) {
            case -1:
                setSlides((prevState) => ({...prevState, begin: true}));
                break;
            case 0:
                setSlides((prevState) => ({ ...prevState, name: true }));
                break;
            case 1:
                setSlides((prevState) => ({ ...prevState, gender: true }));
                break;
            case 2:
                setSlides((prevState) => ({ ...prevState, birth: true }));
                break;
            case 3:
                setSlides((prevState) => ({ ...prevState, end: true }));
                break;
            default:
                break;
        }
    }, [step]);

    return (
        <Grid>
            {/* <Slide direction="right" in={slides.begin}>
                <Typography>Début</Typography>
            </Slide>
            <Slide direction="right" in={slides.name}>
                <Typography>Entrez votre nom complet</Typography>
                <NameField name={name} setName={setName} error={nameError} />
            </Slide>
            <Slide direction="right" in={slides.gender}>
                <Typography>Selectionnez votre genre</Typography>
                <RadioGender gender={gender} setGender={setGender} />
            </Slide>
            <Slide direction="right" in={slides.birth}>
                <Typography>Entrez votre date de naissance</Typography>
                <BirthDate birth={birth} setBirth={setBirth} />
            </Slide>
            <Slide direction="right" in={slides.end}>
                <Typography>Vous êtes {name}, votre genre est {gender} et vous êtes {gender === 'female' ? "née" : "né"} le {new Date(birth).toLocaleDateString()} </Typography>
            </Slide> */}
        </Grid>
    );

    if (step === -1) return (<Typography>Début</Typography>);
    else if (step === 0) return NameForm({ name, setName, error: nameError });
    else if (step === 1) return GenderForm({ gender, setGender });
    else if (step === 2) return BirthForm({ birth, setBirth });
    else if (step >= 3) return EndForm({ name, gender, birth });

    return (<Typography>End</Typography>);
}

export default FormsStep;
