import React, { useState } from 'react';
import { Button, Grid, makeStyles, TextField, Typography } from '@material-ui/core';
import StepperList from '../components/StepperList';
import NameField from '../components/NameField';

const formsStep = (step) => {
    switch (step) {
        case 0:
            return (
                <NameField />
            );
        case 1:
            return (<Typography>Step 2</Typography>);
        case 2:
            return (<Typography>Step 3</Typography>);
        case 3:
            return (<Typography>Last step</Typography>);
        case (step >= 4):
            return (<Typography>Finito</Typography>);
        default:
            return (<Typography>Should not happen</Typography>);
    }
}

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        padding: theme.spacing(2),
    },
}));

const MainPage = () => {
    const classes = useStyles();
    const [step, setStep] = useState(0);

    const nextStep = () => setStep((prevState) => prevState + 1);

    return (
        <div className={classes.root}>
            <Grid container spacing={2} justify="center">
                <Grid item xs={8}>
                    <StepperList currentStep={step} />
                </Grid>
                <Grid item xs={8}>
                    {formsStep(step)}
                </Grid>
                <Grid item xs={8}>
                    <Button
                        variant="contained"
                        onClick={() => nextStep()}
                        >
                        Suivant
                </Button>
                </Grid>
            </Grid>
        </div>
    );
}

export default MainPage;
